package com.basics;

public class If_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 if(condition)
		 {true}
		 else{
		 false}
		 */
		int age = 19;
		
		if(age == 19)
		{
			System.out.println("hey! teenager");
		}
		else 
		{
			System.out.println("bye! teenager");
		}
		
		if(age >= 20)
		{
			System.out.println("hey! adult");
		}
		else
		{
			System.out.println("huh");
		}
		
	}

}
